import "./ProfileCard.css";

import { useState, useEffect } from "react";
import Select from "react-select";

import OCRGrid from "./OcrGrid";
import CharacterStat from "./CharacterStat";
import FittedText from "./FittedText";

import { character as characterList } from "../Datas/Character";
import { weapon as weaponList } from "../Datas/Weapon";

function ProfileCard() {
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const [weapon, setWeapon] = useState(null);
  const lang = localStorage.getItem("lang") || "kr";

  // 문자열 label + 이미지 경로 저장
  const characterOptions = characterList.map((c) => ({
    value: c.id,
    label: lang === "en" ? c.id : c[lang] || c.id,
    img: `/character/${c.id}/ico.webp`,
  }));

  const weaponOptions = (weaponList[selectedCharacter?.weapon] || []).map(
    (w) => ({
      value: w.id,
      label: lang === "en" ? w.id : w[lang] || w.id,
      img: `/weapon/${selectedCharacter?.weapon}/${w.imgKey}.png`,
    })
  );

  const customStyles = {
    control: (base, state) => ({
      ...base,
      display: "flex",
      height: "100%",
      width: "100%",
      fontSize: "1rem",
      borderColor: state.isFocused ? "#2684FF" : "gray",
      boxShadow: state.isFocused ? "0 0 0 1px #2684FF" : "none",
    }),
    menu: (base) => ({ ...base, zIndex: 1000 }),
    option: (base, state) => ({
      ...base,
      backgroundColor: state.isFocused ? "#f0f0f0" : "white",
      color: "black",
      padding: "10px 12px",
    }),
    dropdownIndicator: (base) => ({ ...base, padding: "4px" }),
    indicatorSeparator: () => ({ display: "none" }),
  };

  return (
      print(weaponOptions),

    <div className="profile-portrait">
      {/* Character, Weapon Select */}
      <div className="profile-dropdown-grid">
        <Select
          className="dropdown"
          options={characterOptions}
          styles={customStyles}
          getOptionLabel={(e) => (
            <div style={{ display: "flex", alignItems: "center" }}>
              <img
                src={e.img}
                alt=""
                style={{ width: "25px", height: "25px", marginRight: "8px" }}
              />
              {e.label}
            </div>
          )}
          onChange={(opt) => {
            setSelectedCharacter(characterList.find((c) => c.id === opt.value));
            setWeapon(null);
          }}
          placeholder="캐릭터 선택"
          isSearchable={false}
        />

        <Select
          className="dropdown"
          options={weaponOptions}
          styles={customStyles}
          getOptionLabel={(e) => (
            <div style={{ display: "flex", alignItems: "center" }}>
              <img
                src={e.img}
                alt=""
                style={{ width: "25px", height: "25px", marginRight: "8px" }}
              />
              {e.label}
            </div>
          )}
          onChange={(opt) => setWeapon(opt?.value || null)}
          placeholder="무기 선택"
          isSearchable={false}
          isDisabled={!selectedCharacter}
        />
      </div>

      {/* Profile Card */}
      <div className="profile-card">
        {/* Character image */}
        <img
          className="profile-card-img"
          src="./character/shorekeeper/stand.png"
          alt="char"
        />
        {/* Character image */}
        <div className="profile-card-stats">
          {/* Weapon Stats */}
          <div className="profile-stats-weapon">
            <img
              className="profile-weapon-img"
              src={`/weapon/${selectedCharacter?.weapon}/${weaponOptions?.imgKey}.png`}
              alt="weapon"
            />
            <div className="profile-weapon">
              <FittedText className="profile-weapon-name">뭇별의 교향곡</FittedText>
              <div className="profile-weapon-stats-container">
                <img
                  className="profile-stat-icon"
                  src="./gem.webp"
                  alt="stat"
                />
                <span className="profile-weapon-stats">576&nbsp;&nbsp;</span>
                <img
                  className="profile-stat-icon"
                  src="./gem.webp"
                  alt="stat"
                />
                <span className="profile-weapon-stats">47.8%</span>
              </div>
            </div>
          </div>
          {/* Final Stats */}
          <div className="profile-stat-grid">
            {/*{Array.from({ length: 8 }).map((_, i) => (
              <CharacterStat key={i} />
            ))}*/}
            <CharacterStat id = {"hp"}/>
            <CharacterStat id = {"atk"}/>
            <CharacterStat id = {"def"}/>
            <CharacterStat id = {"Resonance"}/>
            <CharacterStat id = {"CritRate"}/>
            <CharacterStat id = {"CritDmg"}/>
            <CharacterStat id = {selectedCharacter?.element}/>
            <CharacterStat id = {selectedCharacter?.type + 'Bns'}/>
          </div>
          {/* Final Stats Score */}
          <div className="profile-stats-score-grid">
            <div className="profile-stats-score-slot">

            </div>
            <div className="profile-stats-score-slot">

            </div>
            <div className="profile-stats-score-slot">

            </div>
          </div>
        </div>
        {/* Equipment */}
        <div className="profile-card-equipment-grid" />
      </div>

      <OCRGrid />
    </div>
  );
}

export default ProfileCard;
