import "./ProfileCard.css";
import Select from "react-select";
import { useState } from "react";
import { character as characterList } from "../Datas/Character";
import { weapon as weaponList} from "../Datas/Weapon";

function ProfileCard() {
  const [selectedCharacter, setSelectedCharacter] = useState(null);
  const [weapon, setWeapon] = useState("");
  const lang = localStorage.getItem("lang") || "kr";

  const characterOptions = characterList.map((c) => ({
    value: c.id,
    label: (
      <div style={{ display: "flex", alignItems: "center" }}>
        <img
          src={`/character/${c.id}/ico.webp`}
          alt=""
          style={{ width: "25%", height: "25%", marginRight: "10%" }}
        />
      </div>
    ),
  }));

  const customStyles = {
    control: (base, state) => ({
      ...base,
      display: "flex",
      height: "100%",
      width: "100%",
      fontSize: "1rem",
      borderColor: state.isFocused ? "#2684FF" : "gray",
      boxShadow: state.isFocused ? "0 0 0 1px #2684FF" : "none",
      "&:hover": {
        borderColor: "#2684FF",
      },
    }),
    menu: (base) => ({
      ...base,
      zIndex: 1000,
    }),
    option: (base, state) => ({
      ...base,
      backgroundColor: state.isFocused ? "#f0f0f0" : "white",
      color: "black",
      padding: "10px 12px",
    }),
    dropdownIndicator: (base) => ({
      ...base,
      padding: "4px",
    }),
    indicatorSeparator: () => ({
      display: "none",
    }),
  };

  return (
    <div className="portrait">
      <div className="dropdown-grid">
        <Select
          className="dropdown"
          options={characterOptions}
          styles={customStyles}
          onChange={(opt) => setSelectedCharacter(opt.value)}
          placeholder="캐릭터 선택"
          isSearchable={false}
        />

        <select
          className="dropdown"
          value={weapon}
          onChange={(e) => setWeapon(e.target.value)}>
          <option value="">속성 선택</option>
          <option value="fire">불속성</option>
          <option value="ice">얼음속성</option>
          <option value="lightning">번개속성</option>
        </select>
      </div>

      <div className="profile-card">
        {selectedCharacter && (
          <p>
            선택된 캐릭터:{" "}
            {characterList.find((c) => c.id === selectedCharacter)[lang]}
          </p>
        )}
      </div>
    </div>
  );
}

export default ProfileCard;
