import Select from "react-select";
import "./DropSlot.css";
import { FixedStats } from "../Datas/Stats";

function DropSlot({ type, value }) {
  const lang = "kr";

  // 속성 드롭다운 옵션 만들기
  const statOptions = FixedStats.map((stat) => ({
    value: stat.id,
    label: stat[lang] || stat.id,
  }));

  // 수치 드롭다운 옵션 만들기
  const getValueOptions = (id) => {
    const stat = FixedStats.find((s) => s.id === id);
    if (!stat) return [];

    const vals = [...(stat.ValueMain || []), ...(stat.SubValue || [])]
      .filter((v) => v != null)
      .map((v) => ({
        value: typeof v === "string" ? parseFloat(v) : v,
        label: typeof v === "string" ? v : v.toString(),
      }));

    return vals;
  };

  // 빨간 테두리 조건: OCR 이후에 값은 있는데, type이 없을 때
  const isInvalid = (idx) =>
    Array.isArray(type) &&
    Array.isArray(value) &&
    value[idx] !== undefined &&
    (type[idx] === undefined || type[idx] === null);

  // Select 스타일
  const getSelectStyle = (highlight = false) => ({
    control: (base) => ({
      ...base,
      border: highlight ? "1px solid red" : "1px solid #ccc",
      boxShadow: highlight ? "0 0 0 1px red" : "none",
      fontSize: "0.6rem",
      minHeight: "1.6rem",
      height: "1.6rem",
      padding: 0,
    }),
    valueContainer: (base) => ({
      ...base,
      height: "1.6rem",
      padding: "0 6px",
    }),
    indicatorsContainer: (base) => ({
      ...base,
      height: "1.6rem",
    }),
    dropdownIndicator: (base) => ({
      ...base,
      padding: 2,
    }),
    input: (base) => ({
      ...base,
      margin: 0,
      padding: 0,
    }),
    singleValue: (base) => ({
      ...base,
      fontSize: "0.6rem",
      lineHeight: "1.6rem",
    }),
    menu: (base) => ({
      ...base,
      zIndex: 100,
      fontSize: "0.6rem",
    }),
    option: (base, state) => ({
      ...base,
      fontSize: "0.6rem",
      padding: "4px 8px",
      backgroundColor: state.isFocused ? "#eee" : "white",
      color: "black",
    }),
  });

  return (
    <div className="equipment-container">
      <div className="dropdown-grid">
        {[0, 1].map((i) => (
          <div className="dropdown-row" key={`main-${i}`}>
            <Select
              options={statOptions}
              styles={getSelectStyle(false)}
              placeholder="속성 선택"
              defaultValue={
                type?.[i]
                  ? statOptions.find((opt) => opt.value === type[i])
                  : null
              }
            />
            <Select
              options={type?.[i] ? getValueOptions(type[i]) : []}
              styles={getSelectStyle(isInvalid(i))}
              placeholder="수치 선택"
              defaultValue={
                type?.[i] && value?.[i]
                  ? { value: value[i], label: value[i] }
                  : null
              }
            />
          </div>
        ))}

        <div className="dropdown-divider" />

        {[0, 1, 2, 3, 4].map((i) => (
          <div className="dropdown-row" key={`sub-${i}`}>
            <Select
              options={statOptions}
              styles={getSelectStyle(false)}
              placeholder="속성 선택"
              defaultValue={
                type?.[i + 2]
                  ? statOptions.find((opt) => opt.value === type[i + 2])
                  : null
              }
            />
            <Select
              options={type?.[i + 2] ? getValueOptions(type[i + 2]) : []}
              styles={getSelectStyle(isInvalid(i + 2))}
              placeholder="수치 선택"
              defaultValue={
                type?.[i + 2] && value?.[i + 2]
                  ? { value: value[i + 2], label: value[i + 2] }
                  : null
              }
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default DropSlot;
