import stringSimilarity from "string-similarity";
import { FixedStats } from "../Datas/Stats";

export function OcrRetouch({ text, lang }) {
  if (!text || !Array.isArray(text)){return { retType: [], retValue: [] }};
  if (!lang) lang = "en";

  const statList = FixedStats.map(stat => stat[lang]).filter(Boolean);

  let startIndex = -1;

  for (let i = 0; i < text.length; i++) {
    const { bestMatch } = stringSimilarity.findBestMatch(text[i], statList);
    if (bestMatch.rating > 0.8) {
      startIndex = i;
      break;
    }
  }

  console.log(startIndex);

  if (startIndex === -1 || startIndex + 13 >= text.length) {
    return { retType: [], retValue: [] };
  }

  const retType = [];
  const retValue = [];

  for (let i = 0; i < 14; i += 2) {
    const key = text[startIndex + i];
    const val = text[startIndex + i + 1];

    const { bestMatch } = stringSimilarity.findBestMatch(key, statList);
    const matched = bestMatch.rating > 0.8 ? bestMatch.target : null;

    if (matched) {
      retType.push(matched);
      retValue.push(val);
    }
  }

  console.log({ retType, retValue })
  return { retType, retValue };
}
