import stringSimilarity from "string-similarity";
import { FixedStats as FixedStats } from "../Datas/Stats";

export function OcrRetouch({ text, lang }) {
  if (!text || !Array.isArray(text)) return [];
  if (!lang) lang = "en";

  var retType = [];
  var retValue = [];

  const statList = FixedStats.map((s) => s?.[lang]);
  const replaced = text.map((item) => {
    const { bestMatch } = stringSimilarity.findBestMatch(item, statList);
    const matched = bestMatch.rating > 0.9 ? bestMatch.target : item;
    return matched;
  });

  for (var i = 0; i < replaced.length; i++) {
    console.log(replaced[i]);
    try {
      var temp = statList.find((t) => t.replaced[i]);
      if (temp != null) {
        for (var j; j < 7; j++){
            retType[j] = replaced[i + (2 * j)];
            retValue[j] = replaced[i + (2 * j) + 1];
        }
        break;
      }
    } catch {
      continue;
    }
  }

  return {retType, retValue};
}
