import { useState } from "react";
import "./DropGrid.css";
import DropSlot from "./DropSlot";

function DropGrid() {
  const [selected, setSelected] = useState(0); // 0~4

  return (
    <div className="drop-grid-wrapper">
      {/* 탭 버튼 */}
      <div className="drop-grid-tab-row">
        {[0, 1, 2, 3, 4].map((i) => (
          <button
            key={i}
            className={`drop-grid-tab-btn ${selected === i ? "active" : ""}`}
            onClick={() => setSelected(i)}
          >
            Slot {i + 1}
          </button>
        ))}
      </div>

      {/* 선택된 슬롯만 표시 */}
      <div className="drop-grid-slot-container">
        <DropSlot index={selected} />
      </div>
    </div>
  );
}

export default DropGrid;
