import { useState } from "react";

function OCRPage() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
    setResult(null); // ÏÉà Ïù¥ÎØ∏ÏßÄ Ïò¨Î¶¥ Îïê Í≤∞Í≥º Ï¥àÍ∏∞Ìôî
  };

  const handleUpload = async () => {
    if (!image) return alert("Ïù¥ÎØ∏ÏßÄÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî!");

    const formData = new FormData();
    formData.append("file", image);

    setLoading(true);

    try {
      const res = await fetch("http://localhost:8000/ocr", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setResult(data);
    } catch (err) {
      alert("OCR ÏöîÏ≤≠ Ïã§Ìå®");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="ocr-container">
      <h2>üßæ OCR Î∂ÑÏÑù</h2>

      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleUpload} disabled={!image || loading}>
        {loading ? "Î∂ÑÏÑù Ï§ë..." : "OCR ÏöîÏ≤≠"}
      </button>

      {result && (
        <div className="ocr-result">
          <h3>Í≤∞Í≥º:</h3>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default OCRPage;
