import { useState } from "react";
import { FixedStats } from "../Datas/Stats";

function DropSlot({ index }) {
  const [mainStats, setMainStats] = useState(["", ""]);
  const [mainValues, setMainValues] = useState(["", ""]);
  const [subStats, setSubStats] = useState(Array(5).fill(""));
  const [subValues, setSubValues] = useState(Array(5).fill(""));

  const getMainValues = (stat) => statMainValues[stat] || [];
  const getSubValues = (stat) => statSubValues[stat] || [];

  return (
    <div className="slot-wrapper">
      <div className="slot-label">에코 {index + 1}</div>
      <div className="dropdown-grid-vertical">
        {[0, 1].map((i) => (
          <React.Fragment key={`main-${i}`}>
            <select
              value={mainStats[i]}
              onChange={(e) => {
                const newStats = [...mainStats];
                newStats[i] = e.target.value;
                setMainStats(newStats);
              }}>
              <option value="">속성 선택</option>
              {fixedMainStats.map((stat) => (
                <option key={stat} value={stat}>
                  {stat}
                </option>
              ))}
            </select>

            <select
              value={mainValues[i]}
              onChange={(e) => {
                const newVals = [...mainValues];
                newVals[i] = e.target.value;
                setMainValues(newVals);
              }}>
              <option value="">수치 선택</option>
              {getMainValues(mainStats[i]).map((val) => (
                <option key={val} value={val}>
                  {val}
                </option>
              ))}
            </select>
          </React.Fragment>
        ))}

        <div className="dropdown-divider" />

        {[0, 1, 2, 3, 4].map((i) => (
          <React.Fragment key={`sub-${i}`}>
            <select
              value={subStats[i]}
              onChange={(e) => {
                const newStats = [...subStats];
                newStats[i] = e.target.value;
                setSubStats(newStats);
              }}>
              <option value="">속성 선택</option>
              {fixedSubStats.map((stat) => (
                <option key={stat} value={stat}>
                  {stat}
                </option>
              ))}
            </select>

            <select
              value={subValues[i]}
              onChange={(e) => {
                const newVals = [...subValues];
                newVals[i] = e.target.value;
                setSubValues(newVals);
              }}>
              <option value="">수치 선택</option>
              {getSubValues(subStats[i]).map((val) => (
                <option key={val} value={val}>
                  {val}
                </option>
              ))}
            </select>
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

export default DropSlot;
