import Select from 'react-select';
import './DropSlot.css';
import { useState } from 'react';

function DropSlot({ index }) {
  const [cost, setCost] = useState(null);

  const costOptions = [
    { value: 0, label: 'Cost 4' },
    { value: 1, label: 'Cost 3' },
    { value: 2, label: 'Cost 1' }
  ];

  const mainOptions = [{ value: '', label: '속성 선택' }];
  const valueOptions = [{ value: '', label: '수치 선택' }];

  const selectStyle = {
    control: (base) => ({
      ...base,
      minHeight: 32,
      fontSize: '0.9rem',
    }),
    menu: (base) => ({
      ...base,
      zIndex: 100,
    }),
  };

  return (
    <div className="equipment-container">
      <div className="slot-header">
        <span className="slot-label">장비 {index + 1}</span>
        <div className="cost-select">
          <Select
            options={costOptions}
            styles={selectStyle}
            value={costOptions.find((opt) => opt.value === cost)}
            onChange={(opt) => setCost(opt.value)}
            placeholder="Cost 선택"
            isSearchable={false}
          />
        </div>
      </div>

      <div className="dropdown-grid">
        {[0, 1].map((i) => (
          <div className="dropdown-row" key={`main-${i}`}>
            <Select options={mainOptions} styles={selectStyle} placeholder="속성 선택" />
            <Select options={valueOptions} styles={selectStyle} placeholder="수치 선택" />
          </div>
        ))}

        <div className="dropdown-divider" />

        {[0, 1, 2, 3, 4].map((i) => (
          <div className="dropdown-row" key={`sub-${i}`}>
            <Select options={mainOptions} styles={selectStyle} placeholder="속성 선택" />
            <Select options={valueOptions} styles={selectStyle} placeholder="수치 선택" />
          </div>
        ))}
      </div>
    </div>
  );
}

export default DropSlot;
