import { useState } from "react";
import { FixedStats } from "../Datas/Stats"; // 경로에 따라 조정

function DropSlot({ index }) {
  const [selected, setSelected] = useState({
    mainStats: ["", ""],
    mainValues: ["", ""],
    subStats: Array(5).fill(""),
    subValues: Array(5).fill(""),
  });

  const getMainValues = (statId) => {
    const stat = FixedStats.find(s => s.id === statId);
    return stat?.ValueMain?.filter(Boolean) || [];
  };

  const getSubValues = (statId) => {
    const stat = FixedStats.find(s => s.id === statId);
    return stat?.ValueSub?.filter(Boolean) || [];
  };

  return (
    <div className="ocr-container">
      <div className="stat-label">장비 {index + 1}</div>
      <div className="stat-grid">
        {[0, 1].map((i) => (
          <div key={`main-${i}`} className="stat-row">
            <select
              value={selected.mainStats[i]}
              onChange={(e) => {
                const updated = [...selected.mainStats];
                updated[i] = e.target.value;
                setSelected({ ...selected, mainStats: updated });
              }}>
              <option value="">속성 선택</option>
              {FixedStats.map((s) => (
                <option key={s.id} value={s.id}>{s.kr}</option>
              ))}
            </select>

            <select
              value={selected.mainValues[i]}
              onChange={(e) => {
                const updated = [...selected.mainValues];
                updated[i] = e.target.value;
                setSelected({ ...selected, mainValues: updated });
              }}>
              <option value="">수치 선택</option>
              {getMainValues(selected.mainStats[i]).map((val) => (
                <option key={val} value={val}>{val}</option>
              ))}
            </select>
          </div>
        ))}

        <hr className="dropdown-divider" />

        {[0, 1, 2, 3, 4].map((i) => (
          <div key={`sub-${i}`} className="stat-row">
            <select
              value={selected.subStats[i]}
              onChange={(e) => {
                const updated = [...selected.subStats];
                updated[i] = e.target.value;
                setSelected({ ...selected, subStats: updated });
              }}>
              <option value="">속성 선택</option>
              {FixedStats.map((s) => (
                <option key={s.id} value={s.id}>{s.kr}</option>
              ))}
            </select>

            <select
              value={selected.subValues[i]}
              onChange={(e) => {
                const updated = [...selected.subValues];
                updated[i] = e.target.value;
                setSelected({ ...selected, subValues: updated });
              }}>
              <option value="">수치 선택</option>
              {getSubValues(selected.subStats[i]).map((val) => (
                <option key={val} value={val}>{val}</option>
              ))}
            </select>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DropSlot;
