import Select from 'react-select';
import './DropSlot.css';

function DropSlot({ index }) {
  // 공통 placeholder용 배열
  const mainOptions = [{ value: '', label: '속성 선택' }];
  const valueOptions = [{ value: '', label: '수치 선택' }];

  // custom style for react-select
  const selectStyle = {
    control: (base) => ({
      ...base,
      minHeight: 32,
      fontSize: '0.7rem',
    }),
    menu: (base) => ({
      ...base,
      zIndex: 100,
    }),
  };

  return (
    <div className="ocr-container">
      <div className="slot-label">장비 {index + 1}</div>

      <div className="dropdown-grid">
        {/* 주옵션 2개 */}
        {[0, 1].map((i) => (
          <div className="dropdown-row" key={`main-${i}`}>
            <Select options={mainOptions} styles={selectStyle} placeholder="속성 선택" />
            <Select options={valueOptions} styles={selectStyle} placeholder="수치 선택" />
          </div>
        ))}

        <div className="dropdown-divider" />

        {/* 부옵션 5개 */}
        {[0, 1, 2, 3, 4].map((i) => (
          <div className="dropdown-row" key={`sub-${i}`}>
            <Select options={mainOptions} styles={selectStyle} placeholder="속성 선택" />
            <Select options={valueOptions} styles={selectStyle} placeholder="수치 선택" />
          </div>
        ))}
      </div>
    </div>
  );
}

export default DropSlot;
